import { Component } from '@angular/core';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.scss']
})
export class NavbarComponent {

  public navbarItemList = [
    {
      value: 'About',
      link: '#'
    },
    {
      value: 'Portfolio',
      link: '#'
    },
    {
      value: 'Contact',
      link: '#'
    }
  ]

}
